//发送异步ajax请求
/*
    封装了axios库
    函数的返回值是一个promise对象
*/

import axios from 'axios'

export default function ajax(url,data={},type='GET'){
    if(type==='GET'){      //发送GET请求
        return axios.get(url,{     //配置对象
            params:data        //指定请求参数
        })
    }else{    //发送POST请求
        return axios.post(url,data)
    }
}
