import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import { Menu } from 'antd'; 

import menuList from '../../config/menuConfig'

import logo from '../../assets/images/logo.png'
import './index.less'

const SubMenu = Menu.SubMenu;


/*
  左侧导航栏
 */
export default class LeftNav extends Component {

  /*
    根据menu的数据生成对应的标签数组
   */
  getMenuNodes =(menuList)=>{
    return menuList.map(item =>{

      if(!item.children){
        return (
          <Menu.Item key={item.key}>
            <Link to={item.key}>
              <item.icon />
              <span>{item.title}</span>
            </Link>
          </Menu.Item>
        )
      }else{
        return(
          <SubMenu
            key={item.key}
            title={
              <span>
                <item.icon />
                <span>{item.title}</span>
              </span>
            }
          >
            <Menu.Item key="3">
              <Link to="/product">
              <item.icon />
                <span>商品管理</span>
              </Link>
            </Menu.Item>
          </SubMenu>  
        )
      }
      
    })
  }

  render() {
    return (
        <div className='left-nav'>
          <Link to='/' className='left-nav-header'>
            <img src={logo} alt="logo"/>
            <h1>后台管理</h1>
          </Link>

          <Menu
            defaultSelectedKeys={['1']}
            defaultOpenKeys={['sub1']}
            mode="inline"
            theme="dark"
          >
          {/* {this.getMenuNodes(menuList)} */}
          {/* 上面这个方法为动态生成Menu菜单项的方法，有了这个方法，我们就不用写下面这一大堆的东西了 */}
          {/* <Menu.Item key="1">
            <Link to="/home">
              <HomeOutlined />
              <span>首页</span>
            </Link>
          </Menu.Item>
 
          <SubMenu
            key="2"
            title={
              <span>
                <AccountBookOutlined />
                <span>商品</span>
              </span>
            }
          >
            <Menu.Item key="3">
              <Link to="/product">
                <AccountBookOutlined />
                <span>商品管理</span>
              </Link>
            </Menu.Item>
            <Menu.Item key="4">
              <Link to="/category">
                <AccountBookOutlined />
                <span>品类管理</span>
              </Link>
            </Menu.Item>
          </SubMenu> */}
          
          {
            this.getMenuNodes(menuList)
          }
          
        </Menu>
        </div>
    )
  }
}
